create table categorie(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
nom varchar(20)
)Engine=InnoDB;

create table film(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
titre varchar(50),
origine varchar(20),
realisation varchar(20),
duree varchar(10),
acteurs varchar(150),
datesortie datetime,
description varchar(1000),
cheminimg varchar(100)
)Engine=InnoDB;

create table linkFilmCat(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
idCat int,
idFilm int,
FOREIGN KEY (idCat) REFERENCES categorie(id),
FOREIGN KEY (idFilm) REFERENCES film(id)
);


create table salle(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
nom varchar(10),
numero int,
nbrPlace int
)Engine=InnoDB;

create table programme(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
idFilm int,
idSalle int,
daty date,
heureDebut varchar(5),
heureFin varchar(5),
prixUnitaire double,
prioritaire int,
FOREIGN KEY (idFilm) REFERENCES film(id),
FOREIGN KEY (idSalle) REFERENCES salle(id)
)Engine=InnoDB;


create table membre(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
pseudo varchar(20),
email varchar(20),
password varchar(20),
autorisation int,
cheminimg varchar(100)
)Engine=InnoDB;


create table reservation(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
idMembre int,
idProgramme int,
nbrPlac int,
montant double,
FOREIGN KEY (idMembre) REFERENCES membre(id),
FOREIGN KEY (idProgramme) REFERENCES programme(id)
)Engine=InnoDB;


create table commentaires(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
idFilm int,
texte varchar(100),
idMembre int,
FOREIGN KEY (idFilm) REFERENCES film(id),
FOREIGN KEY (idMembre) REFERENCES membre(id)
)Engine=InnoDB;

create table billet(
id INTEGER NOT NULL AUTO_INCREMENT primary key,
idReservation int,
FOREIGN KEY (idReservation) REFERENCES reservation(id)

);

